import React from 'react';
import { mount } from 'enzyme';

import LoginPage from './index';

let wrapper;

beforeEach(() => {
    wrapper = mount(<LoginPage />);
});

describe('LoginPage component', () => {

    const username = 'Username';
    const password = 'Password';

    it('renders without crashing', () => {
        mount(<LoginPage />);
    });

    it('renders two input fields', () => {
        expect(wrapper.find('TextField')).toHaveLength(2);
    });

    it('renders one button', () => {
        expect(wrapper.find('Button')).toHaveLength(1);
    });

    it('renders the password button (IconButton)', () => {
        expect(wrapper.find('IconButton')).toHaveLength(1);
    });

    it('renders one logo image', () => {
       expect(wrapper.find('img')).toHaveLength(1);
    });

    it('renders a Username label', () => {
        expect(wrapper.find('Typography').at(0).text()).toEqual(expect.stringMatching(username));
    });

    it('renders a Password label', () => {
        expect(wrapper.find('Typography').at(1).text()).toEqual(expect.stringMatching(password));
    });

});